INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'es_ES', 'Por favor, valide su {WEB_TITLE} cuenta', '<p>Hola {USER_NAME},</p><p>Por favor, valide su registro haciendo clic en el siguiente enlace: {VALIDATION_LINK}</p><p>¡Gracias!</p><p>Saludos,</p><p>{WEB_LINK}</p>');
