INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'es_ES', 'Mira lo que he descubierto en la {WEB_TITLE}', '<p>Hola {FRIEND_NAME},</p><p>Tu amigo{USER_NAME} quiere compartir esta lista con usted<a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Mensaje:</p><p>{COMMENT}</p><p>Saludos,</p><p>{WEB_TITLE}</p>');
