INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'es_ES', '{WEB_TITLE} - Alguien ha comentado sobre su anuncio', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'es_ES', '{WEB_TITLE} - Cuenta de administrador creada con éxito!', '<p>Hola {ADMIN_NAME},</p><p>El administrador de {WEB_LINK} ha creado una cuenta para ti,</p><ul><li>El nombre de usuario: {USERNAME}</li><li>Contraseña: {PASSWORD}</li></ul><p>Puedes acceder al panel de administración aquí {WEB_ADMIN_LINK}.</p><p>Gracias!</p><p>Saludos,</p>');
